import { useState } from "react";
import EliteEvents from "./EliteEvents";
import NonTechnicalEvents from "./NonTechnicalEvents";
import TechnicalEvents from "./TechnicalEvents";
import logo from "../../assets/logo.png";
// import { GiTimeSynchronization } from "react-icons/gi";
import { HiOutlineHome } from "react-icons/hi";
import { AiOutlineDoubleRight } from "react-icons/ai";
import { TbUnlink } from "react-icons/tb";
import { SiRiotgames } from "react-icons/si";
import { VscOrganization } from "react-icons/vsc";
import { Helmet } from "react-helmet";
import { motion } from "framer-motion";
import { Link } from "react-router-dom";

export default function AllEvents() {
  const [open, setOpen] = useState(false);
  const [eventType, setEventType] = useState(1);
  return (
    <div className="bg-black min-h-screen py-4 sm:py-8">
      <Helmet>
        <meta charSet="utf-8" />
        <title>ASTRANOVA | Events</title>
      </Helmet>
      <nav className="flex items-center justify-between pb-10 md:px-4">
        <Link to={"/"}>
          <motion.img
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, amount: 0.5 }}
            transition={{ duration: 0.8 }}
            variants={{
              hidden: { opacity: 0, x: 120 },
              visible: { opacity: 1, x: 0 },
            }}
            src={logo}
            alt="celestra-logo"
            className="lg:absolute lg:left-4 lg:top-6 w-44 md:w-52 rounded-full"
          />
        </Link>
        {/* Menu */}
        <motion.div
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.5 }}
          transition={{ duration: 0.8 }}
          variants={{
            hidden: { opacity: 0, x: -120 },
            visible: { opacity: 1, x: 0 },
          }}
          onClick={() => setOpen(!open)}
          className="relative flex  items-center gap-2 mb-3 mr-4 lg:absolute lg:right-10 lg:top-24 cursor-pointer"
        >
          <p className="text-xl text-white AstranovaHeroFont tracking-widest">
            {" "}
            {open ? (
              <motion.div
                whileTap={{ scale: 0.6 }}
                className="flex items-center"
              >
                <span className="text-4xl text-purple-500">C</span>
                <span className="mt-1">lose</span>
              </motion.div>
            ) : (
              <motion.div whileTap={{ scale: 0.6 }}>
                <span className="text-4xl text-purple-500">M</span>
                <span>enu</span>
              </motion.div>
            )}
          </p>
          <span className={`mt-2 ${open && "rotate-90"}`}>
            <AiOutlineDoubleRight size={20} color="white" />
          </span>
          {/* DropDown Menu */}
          {open && (
            <motion.div
              initial={{ opacity: 0, scale: 0.2 }}
              animate={{ opacity: 1, scale: 1 }}
              exit={{ opacity: 0, scale: 0.6 }}
              className="absolute top-14 z-100 right-0 opacity-95 w-72 h-auto bg-white rounded-lg AstranovaHeroFont "
              style={{
                zIndex: 10,
              }}
            >
              <ul>
                <Link
                  to={"/"}
                  className="flex text-lg px-4 py-4 items-center gap-3 rounded-t-lg  transition-all duration-300 ease-in-out  hover:bg-purple-300 "
                >
                  <HiOutlineHome size={20} />{" "}
                  <div className="text-purple-500 text-2xl ">
                    H<span className="text-black text-xl">ome</span>{" "}
                  </div>
                </Link>
                <Link
                  to={"/about-us"}
                  className="flex text-lg px-4 py-4 items-center gap-3 transition-all duration-300 ease-in-out hover:bg-purple-300 "
                >
                  <TbUnlink size={20} />{" "}
                  <div className="text-purple-500 text-2xl ">
                    A<span className="text-black text-xl">bout Us</span>{" "}
                  </div>
                </Link>
                <Link
                  to={"/games/online"}
                  className="flex text-lg px-4 py-4 items-center gap-3 transition-all duration-300 ease-in-out hover:bg-purple-300 "
                >
                  <SiRiotgames size={20} />{" "}
                  <div className="text-purple-500 text-2xl ">
                    G<span className="text-black text-xl">ames</span>{" "}
                  </div>
                </Link>
                <Link
                  to={"#"}
                  className="flex text-lg px-4 py-4 items-center gap-3 transition-all duration-300 ease-in-out hover:bg-purple-300 "
                >
                  <GiTimeSynchronization size={20} />{" "}
                  <div className="text-purple-500 text-2xl ">
                    C<span className="text-black text-xl">hronoline</span>{" "}
                  </div>
                </Link>
                {/* <Link
                  to={"/team"}
                  className="flex text-lg px-4 py-4 items-center gap-3 transition-all duration-300 ease-in-out hover:bg-purple-300 "
                >
                  <VscOrganization size={20} />{" "}
                  <div className="text-purple-500 text-2xl ">
                    O<span className="text-black text-xl">ur Team</span>{" "}
                  </div>
                </Link> */}
                <Link
                  to={"/contact-us"}
                  className="flex text-lg px-4 py-4 items-center gap-3 transition-all duration-300 ease-in-out hover:bg-purple-300 "
                >
                  <VscOrganization size={20} />{" "}
                  <div className="text-purple-500 text-2xl ">
                    C<span className="text-black text-xl">ontact Us</span>{" "}
                  </div>
                </Link>
              </ul>
            </motion.div>
          )}
        </motion.div>
      </nav>
      <div className="mx-auto max-w-7xl  px-6 lg:px-8">
        {/* Events selection Section */}
        <motion.div
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.5 }}
          transition={{ duration: 0.7 }}
          variants={{
            hidden: { opacity: 0, x: 120 },
            visible: { opacity: 1, x: 0 },
          }}
          className="text-white AstranovaHeroFont mb-20 mt-6 flex gap-12  justify-around sm:justify-center lg:gap-12 flex-wrap lg:flex-nowrap "
        >
          <motion.span
            whileTap={{ scale: 0.8 }}
            onClick={() => setEventType(1)}
            className={` ${
              eventType === 1
                ? "bg-purple-600 text-[#111111]"
                : "border  border-purple-600"
            }
              px-6 py-1 flex items-center justify-center rounded-xl text-xl tracking-widest cursor-pointer `}
          >
            Technical
          </motion.span>
          <motion.span
            whileTap={{ scale: 0.8 }}
            onClick={() => setEventType(2)}
            className={` ${
              eventType === 2
                ? "bg-purple-600 text-[#111111]"
                : "border  border-purple-600"
            }
              px-6 py-4  flex items-center justify-center rounded-xl text-xl tracking-widest cursor-pointer `}
          >
            Non-Technical
          </motion.span>
          <motion.span
            whileTap={{ scale: 0.8 }}
            onClick={() => setEventType(3)}
            className={` ${
              eventType === 3
                ? "bg-purple-600 text-[#111111]"
                : "border  border-purple-600"
            }
              px-6 py-4  flex items-center justify-center rounded-xl text-xl tracking-widest cursor-pointer `}
          >
            Golden-byte Events
          </motion.span>
          <Link to={"/games/online"}>
            <motion.span
              whileTap={{ scale: 0.8 }}
              className={` border  border-purple-600 px-6 py-4  flex items-center justify-center rounded-xl text-xl tracking-widest cursor-pointer `}
            >
              Games
            </motion.span>
          </Link>
        </motion.div>
      </div>
      {/* Events Section */}
      {eventType === 1 && <TechnicalEvents />}
      {eventType === 2 && <NonTechnicalEvents />}
      {eventType === 3 && <EliteEvents />}
    </div>
  );
}
